var thumb = require('./src/thumbnail.js').thumb;

thumb({
  source: 'test-src',
  destination: 'test-dest',
  concurrency: 1
}, function(err, stdout, stderr) {
  if (err !== undefined) {
    console.log('such error callback', err.toString())
  } else {
    console.log('such success callback');
  }
}).then(function() {
  console.log('done') ;
}).catch(function(a) {
  console.log('catch', a.toString())
});
